import React, { Component } from "react";
import java from "../images/languages/Java.jpg";
import htmlcss from "../images/languages/html-css.jpg";
import jsts from "../images/languages/js-ts.png";
import graphql from "../images/languages/graphql.png";
import cplusplus from "../images/languages/cplusplus.jpg";

class LanguagesPanel extends Component {
  constructor() {
    super();
    this.state = {
      java: "close",
      htmlcss: "close",
      jsts: "close",
      graphql: "close",
      cplusplus: "close"
    };
  }

  openPanel = div => {
    console.log(div.target.alt);
    if (div.target.alt === "close") {
      this.setState({
        [div.target.alt]: "open"
      });
    }
    // else {
    // this.setState({
    //   [div.target.alt]: "close"
    // });
    // }
    console.log(this.state[div.target.alt]);
  };

  render() {
    return (
      <div className="panel-container">
        <div className="close">
          <img src={java} alt="java" id="java"  />
        </div>
        <div className="close">
          <img src={htmlcss} alt="htmlcss" onClick={this.openPanel} />
        </div>
        <div className="open">
          <img src={jsts} alt="jsts" onClick={this.openPanel} />
        </div>
        <div className="close">
          <img src={graphql} alt="graphql" onClick={this.openPanel} />
        </div>
        <div className="close">
          <img src={cplusplus} alt="cplusplus" onClick={this.openPanel} />
        </div>
      </div>
    );
  }
}

export default LanguagesPanel;
